server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertResumeSchema, insertJobDescriptionSchema } from "@shared/schema";
import { parseResume, validateFileType, validateFileSize } from "./services/resumeParser";
import { analysisEngine } from "./services/analysisEngine";
import multer from "multer";
import path from "path";
import fs from "fs";

// Configure multer for file uploads
const upload = multer({
  dest: 'uploads/',
  limits: {
    fileSize: 10 * 1024 * 1024, // 10MB limit
  },
  fileFilter: (req, file, cb) => {
    if (validateFileType(file.mimetype)) {
      cb(null, true);
    } else {
      cb(new Error('Invalid file type. Only PDF and DOCX files are allowed.'));
    }
  },
});

export async function registerRoutes(app: Express): Promise<Server> {
  // Upload resume endpoint
  app.post("/api/resume/upload", upload.single('resume'), async (req, res) => {
    try {
      if (!req.file) {
        return res.status(400).json({ error: "No file uploaded" });
      }

      // Validate file size
      if (!validateFileSize(req.file.size)) {
        fs.unlinkSync(req.file.path); // Clean up uploaded file
        return res.status(400).json({ error: "File size exceeds 10MB limit" });
      }

      // Parse the resume
      const resumeText = await parseResume(req.file.path, req.file.mimetype);

      // Create resume record
      const resumeData = {
        filename: req.file.originalname,
        originalText: resumeText,
        fileSize: req.file.size,
      };

      const validatedData = insertResumeSchema.parse(resumeData);
      const resume = await storage.createResume(validatedData);

      // Clean up uploaded file
      fs.unlinkSync(req.file.path);

      res.json(resume);
    } catch (error) {
      console.error("Resume upload error:", error);
      if (req.file) {
        fs.unlinkSync(req.file.path); // Clean up on error
      }
      res.status(500).json({ error: "Failed to process resume upload" });
    }
  });

  // Create job description endpoint
  app.post("/api/job-description", async (req, res) => {
    try {
      const validatedData = insertJobDescriptionSchema.parse(req.body);
      const jobDescription = await storage.createJobDescription(validatedData);
      res.json(jobDescription);
    } catch (error) {
      console.error("Job description creation error:", error);
      res.status(400).json({ error: "Invalid job description data" });
    }
  });

  // Analyze resume endpoint
  app.post("/api/analyze", async (req, res) => {
    try {
      const { resumeId, jobDescriptionId } = req.body;

      if (!resumeId || !jobDescriptionId) {
        return res.status(400).json({ error: "Resume ID and Job Description ID are required" });
      }

      // Get resume and job description
      const [resume, jobDescription] = await Promise.all([
        storage.getResume(resumeId),
        storage.getJobDescription(jobDescriptionId)
      ]);

      if (!resume) {
        return res.status(404).json({ error: "Resume not found" });
      }

      if (!jobDescription) {
        return res.status(404).json({ error: "Job description not found" });
      }

      // Perform analysis
      const analysisResult = await analysisEngine.analyzeResume(
        resume.originalText,
        jobDescription.description
      );

      // Save analysis
      const analysis = await storage.createAnalysis({
        resumeId,
        jobDescriptionId,
        overallScore: analysisResult.overallScore,
        keywordMatchScore: analysisResult.keywordMatchScore,
        formatScore: analysisResult.formatScore,
        skillsMatchScore: analysisResult.skillsMatchScore,
        foundKeywords: analysisResult.foundKeywords,
        missingKeywords: analysisResult.missingKeywords,
        recommendations: analysisResult.recommendations,
        formattingChecks: analysisResult.formattingChecks,
        skillsGap: analysisResult.skillsGap,
      });

      res.json({
        analysis,
        result: analysisResult
      });
    } catch (error) {
      console.error("Analysis error:", error);
      res.status(500).json({ error: "Failed to analyze resume" });
    }
  });

  // Get analysis endpoint
  app.get("/api/analysis/:id", async (req, res) => {
    try {
      const analysis = await storage.getAnalysis(req.params.id);

      if (!analysis) {
        return res.status(404).json({ error: "Analysis not found" });
      }

      res.json(analysis);
    } catch (error) {
      console.error("Get analysis error:", error);
      res.status(500).json({ error: "Failed to retrieve analysis" });
    }
  });

  // Get recent analyses
  app.get("/api/analyses/recent", async (req, res) => {
    try {
      const limit = parseInt(req.query.limit as string) || 10;
      const analyses = await storage.getRecentAnalyses(limit);
      res.json(analyses);
    } catch (error) {
      console.error("Get recent analyses error:", error);
      res.status(500).json({ error: "Failed to retrieve recent analyses" });
    }
  });

  // Get resume endpoint
  app.get("/api/resume/:id", async (req, res) => {
    try {
      const resume = await storage.getResume(req.params.id);

      if (!resume) {
        return res.status(404).json({ error: "Resume not found" });
      }

      res.json(resume);
    } catch (error) {
      console.error("Get resume error:", error);
      res.status(500).json({ error: "Failed to retrieve resume" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { type User, type InsertUser, type Resume, type InsertResume, type JobDescription, type InsertJobDescription, type Analysis, type InsertAnalysis } from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // User operations
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Resume operations
  createResume(resume: InsertResume): Promise<Resume>;
  getResume(id: string): Promise<Resume | undefined>;
  getResumesByUser(userId: string): Promise<Resume[]>;

  // Job description operations
  createJobDescription(jobDescription: InsertJobDescription): Promise<JobDescription>;
  getJobDescription(id: string): Promise<JobDescription | undefined>;

  // Analysis operations
  createAnalysis(analysis: InsertAnalysis): Promise<Analysis>;
  getAnalysis(id: string): Promise<Analysis | undefined>;
  getAnalysesByResume(resumeId: string): Promise<Analysis[]>;
  getRecentAnalyses(limit?: number): Promise<Analysis[]>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private resumes: Map<string, Resume>;
  private jobDescriptions: Map<string, JobDescription>;
  private analyses: Map<string, Analysis>;

  constructor() {
    this.users = new Map();
    this.resumes = new Map();
    this.jobDescriptions = new Map();
    this.analyses = new Map();
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { ...insertUser, id };
    this.users.set(id, user);
    return user;
  }

  async createResume(insertResume: InsertResume): Promise<Resume> {
    const id = randomUUID();
    const resume: Resume = {
      ...insertResume,
      id,
      userId: insertResume.userId || null,
      uploadedAt: new Date(),
    };
    this.resumes.set(id, resume);
    return resume;
  }

  async getResume(id: string): Promise<Resume | undefined> {
    return this.resumes.get(id);
  }

  async getResumesByUser(userId: string): Promise<Resume[]> {
    return Array.from(this.resumes.values()).filter(
      (resume) => resume.userId === userId
    );
  }

  async createJobDescription(insertJobDescription: InsertJobDescription): Promise<JobDescription> {
    const id = randomUUID();
    const jobDescription: JobDescription = {
      ...insertJobDescription,
      id,
      createdAt: new Date(),
    };
    this.jobDescriptions.set(id, jobDescription);
    return jobDescription;
  }

  async getJobDescription(id: string): Promise<JobDescription | undefined> {
    return this.jobDescriptions.get(id);
  }

  async createAnalysis(insertAnalysis: InsertAnalysis): Promise<Analysis> {
    const id = randomUUID();
    const analysis: Analysis = {
      ...insertAnalysis,
      id,
      analyzedAt: new Date(),
    };
    this.analyses.set(id, analysis);
    return analysis;
  }

  async getAnalysis(id: string): Promise<Analysis | undefined> {
    return this.analyses.get(id);
  }

  async getAnalysesByResume(resumeId: string): Promise<Analysis[]> {
    return Array.from(this.analyses.values()).filter(
      (analysis) => analysis.resumeId === resumeId
    );
  }

  async getRecentAnalyses(limit: number = 10): Promise<Analysis[]> {
    const sortedAnalyses = Array.from(this.analyses.values())
      .sort((a, b) => new Date(b.analyzedAt).getTime() - new Date(a.analyzedAt).getTime());
    return sortedAnalyses.slice(0, limit);
  }
}

export const storage = new MemStorage();

